WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 11), filtered_returns AS (SELECT cr.cr_net_loss, cc.cc_call_center_id, cc.cc_name, cc.cc_manager, cd.cd_marital_status, cd.cd_education_status FROM catalog_returns AS cr JOIN filtered_dates AS d ON cr.cr_returned_date_sk = d.d_date_sk JOIN call_center AS cc ON cr.cr_call_center_sk = cc.cc_call_center_sk JOIN customer AS c ON cr.cr_returning_customer_sk = c.c_customer_sk JOIN customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics AS hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE cd.cd_marital_status = 'M' AND cd.cd_education_status = 'Unknown' AND hd.hd_buy_potential LIKE '1001-5000%' AND ca.ca_gmt_offset = -6 UNION ALL SELECT cr.cr_net_loss, cc.cc_call_center_id, cc.cc_name, cc.cc_manager, cd.cd_marital_status, cd.cd_education_status FROM catalog_returns AS cr JOIN filtered_dates AS d ON cr.cr_returned_date_sk = d.d_date_sk JOIN call_center AS cc ON cr.cr_call_center_sk = cc.cc_call_center_sk JOIN customer AS c ON cr.cr_returning_customer_sk = c.c_customer_sk JOIN customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics AS hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE cd.cd_marital_status = 'W' AND cd.cd_education_status = 'Advanced Degree' AND hd.hd_buy_potential LIKE '1001-5000%' AND ca.ca_gmt_offset = -6)
SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM filtered_returns GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status ORDER BY SUM(cr_net_loss) DESC
