WITH customer_total_return AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, sr_reason_sk AS ctr_reason_sk, SUM(SR_REFUNDED_CASH) AS ctr_total_return FROM store_returns JOIN date_dim ON sr_returned_date_sk = d_date_sk WHERE d_year = 2000 AND sr_return_amt / sr_return_quantity BETWEEN 16 AND 75 GROUP BY sr_customer_sk, sr_store_sk, sr_reason_sk), store_avg_returns AS (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS avg_return_threshold FROM customer_total_return GROUP BY ctr_store_sk) SELECT c_customer_id FROM customer_total_return AS ctr1 JOIN store_avg_returns AS avg ON ctr1.ctr_store_sk = avg.ctr_store_sk JOIN store ON s_store_sk = ctr1.ctr_store_sk JOIN customer ON ctr1.ctr_customer_sk = c_customer_sk JOIN customer_demographics ON c_current_cdemo_sk = cd_demo_sk WHERE ctr1.ctr_total_return > avg.avg_return_threshold AND ctr1.ctr_reason_sk BETWEEN 25 AND 28 AND s_state IN ('MI', 'ND', 'TX') AND cd_marital_status IN ('M', 'M') AND cd_education_status IN ('College', 'College') AND cd_gender = 'M' AND c_birth_month = 9 AND c_birth_year BETWEEN 1979 AND 1985 ORDER BY c_customer_id LIMIT 100