WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy BETWEEN 4 AND 4 + 3), item_filter AS (SELECT i_item_sk FROM item WHERE i_category IN ('Children', 'Electronics', 'Men') AND i_manager_id BETWEEN 51 AND 60), store_customers AS (SELECT DISTINCT ss_customer_sk FROM store_sales INNER JOIN date_filter ON ss_sold_date_sk = d_date_sk INNER JOIN item_filter ON ss_item_sk = i_item_sk WHERE ss_sales_price / ss_list_price BETWEEN 11 * 0.01 AND 21 * 0.01), web_or_catalog_customers AS (SELECT ws_bill_customer_sk AS customer_sk FROM web_sales INNER JOIN date_filter ON ws_sold_date_sk = d_date_sk INNER JOIN item_filter ON ws_item_sk = i_item_sk WHERE ws_sales_price / ws_list_price BETWEEN 11 * 0.01 AND 21 * 0.01 UNION ALL SELECT cs_ship_customer_sk AS customer_sk FROM catalog_sales INNER JOIN date_filter ON cs_sold_date_sk = d_date_sk INNER JOIN item_filter ON cs_item_sk = i_item_sk WHERE cs_sales_price / cs_list_price BETWEEN 11 * 0.01 AND 21 * 0.01) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3, cd_dep_count, COUNT(*) AS cnt4, cd_dep_employed_count, COUNT(*) AS cnt5, cd_dep_college_count, COUNT(*) AS cnt6 FROM customer AS c INNER JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk INNER JOIN customer_demographics ON cd_demo_sk = c.c_current_cdemo_sk WHERE ca_county IN ('Hickman County', 'Ohio County', 'Parke County', 'Pointe Coupee Parish', 'Siskiyou County') AND c.c_birth_month IN (3, 8) AND cd_marital_status IN ('S', 'U', 'M') AND cd_education_status IN ('Secondary', 'Advanced Degree', '4 yr Degree') AND cd_gender = 'M' AND EXISTS(SELECT 1 FROM store_customers WHERE ss_customer_sk = c.c_customer_sk) AND EXISTS(SELECT 1 FROM web_or_catalog_customers WHERE customer_sk = c.c_customer_sk) GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100