WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy BETWEEN 3 AND 3 + 2), store_customers AS (SELECT DISTINCT ss_customer_sk FROM store_sales JOIN filtered_dates ON store_sales.ss_sold_date_sk = filtered_dates.d_date_sk WHERE ss_list_price BETWEEN 100 AND 189), web_customers AS (SELECT DISTINCT ws_bill_customer_sk FROM web_sales JOIN filtered_dates ON web_sales.ws_sold_date_sk = filtered_dates.d_date_sk WHERE ws_list_price BETWEEN 100 AND 189), catalog_customers AS (SELECT DISTINCT cs_ship_customer_sk FROM catalog_sales JOIN filtered_dates ON catalog_sales.cs_sold_date_sk = filtered_dates.d_date_sk WHERE cs_list_price BETWEEN 100 AND 189) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM customer AS c JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics ON cd_demo_sk = c.c_current_cdemo_sk WHERE ca_state IN ('IA', 'MO', 'TX') AND cd_marital_status IN ('S', 'S', 'S') AND cd_education_status IN ('Primary', 'Secondary') AND c.c_customer_sk IN (SELECT ss_customer_sk FROM store_customers) AND NOT c.c_customer_sk IN (SELECT ws_bill_customer_sk FROM web_customers) AND NOT c.c_customer_sk IN (SELECT cs_ship_customer_sk FROM catalog_customers) GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating LIMIT 100