WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 2000) SELECT c_customer_sk, c_first_name, c_last_name, COUNT(*) AS cnt FROM store_sales JOIN store_returns ON ss_ticket_number = sr_ticket_number JOIN web_sales ON ss_customer_sk = ws_bill_customer_sk JOIN filtered_dates AS d1 ON sr_returned_date_sk = d1.d_date_sk JOIN date_dim AS d2 ON ws_sold_date_sk = d2.d_date_sk JOIN item ON i_item_sk = ss_item_sk JOIN customer ON ss_customer_sk = c_customer_sk JOIN customer_address ON c_current_addr_sk = ca_address_sk JOIN household_demographics ON c_current_hdemo_sk = hd_demo_sk WHERE i_category IN ('Children', 'Electronics', 'Women') AND d2.d_date BETWEEN d1.d_date AND (d1.d_date + INTERVAL '90 DAY') AND ca_state IN ('GA', 'IA', 'LA', 'MO', 'SD') AND hd_income_band_sk BETWEEN 12 AND 18 AND hd_buy_potential = '0-500' AND ss_sales_price / ss_list_price BETWEEN 34 * 0.01 AND 54 * 0.01 GROUP BY c_customer_sk, c_first_name, c_last_name ORDER BY cnt