example.before_sql:
-- Subquery may be "pulled up" and reordered
      SELECT * FROM fact_table f
      JOIN (
          SELECT * FROM dim_table WHERE active = true
      ) d ON f.dim_id = d.id;

example.after_sql:
-- OFFSET 0 prevents subquery pull-up
      SELECT * FROM fact_table f
      JOIN (
          SELECT * FROM dim_table WHERE active = true
          OFFSET 0
      ) d ON f.dim_id = d.id;
