WITH date_filtered_sales AS (SELECT ws_ship_date_sk, ws_ship_addr_sk, ws_web_site_sk, ws_order_number, ws_ext_ship_cost, ws_net_profit, ws_warehouse_sk FROM web_sales JOIN date_dim ON ws_ship_date_sk = d_date_sk WHERE d_date BETWEEN '1999-02-01' AND CAST('1999-04-02' AS DATE)), multi_warehouse_orders AS (SELECT DISTINCT ws_order_number FROM date_filtered_sales AS ws1 WHERE EXISTS(SELECT 1 FROM web_sales AS ws2 WHERE ws1.ws_order_number = ws2.ws_order_number AND ws1.ws_warehouse_sk <> ws2.ws_warehouse_sk)), returned_orders AS (SELECT DISTINCT wr_order_number FROM web_returns), filtered_sales AS (SELECT dfs.ws_order_number, dfs.ws_ext_ship_cost, dfs.ws_net_profit FROM date_filtered_sales AS dfs JOIN customer_address ON dfs.ws_ship_addr_sk = ca_address_sk JOIN web_site ON dfs.ws_web_site_sk = web_site_sk WHERE ca_state = 'IL' AND web_company_name = 'pri')
SELECT COUNT(DISTINCT fs.ws_order_number) AS "order count", SUM(fs.ws_ext_ship_cost) AS "total shipping cost", SUM(fs.ws_net_profit) AS "total net profit" FROM filtered_sales AS fs JOIN multi_warehouse_orders AS mwo ON fs.ws_order_number = mwo.ws_order_number LEFT JOIN returned_orders AS ro ON fs.ws_order_number = ro.wr_order_number WHERE ro.wr_order_number IS NULL ORDER BY COUNT(DISTINCT fs.ws_order_number) LIMIT 100