WITH filtered_returns AS (SELECT sr_customer_sk, sr_store_sk, SR_FEE FROM store_returns JOIN date_dim ON sr_returned_date_sk = d_date_sk JOIN store ON sr_store_sk = s_store_sk WHERE d_year = 2000 AND s_state = 'SD'), customer_total_return_sd AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, SUM(SR_FEE) AS ctr_total_return FROM filtered_returns GROUP BY sr_customer_sk, sr_store_sk), store_avg_sd AS (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS store_avg_threshold FROM customer_total_return_sd GROUP BY ctr_store_sk) SELECT c_customer_id FROM customer_total_return_sd ctr1 JOIN store_avg_sd sas ON ctr1.ctr_store_sk = sas.ctr_store_sk JOIN customer ON ctr1.ctr_customer_sk = c_customer_sk WHERE ctr1.ctr_total_return > sas.store_avg_threshold ORDER BY c_customer_id LIMIT 100