WITH store_2001 AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, c.c_birth_country AS customer_birth_country, 2001 AS dyear, SUM(ss.ss_ext_list_price - ss.ss_ext_discount_amt) AS year_total, 's' AS sale_type FROM customer c, store_sales ss, date_dim d WHERE c.c_customer_sk = ss.ss_customer_sk AND ss.ss_sold_date_sk = d.d_date_sk AND d.d_year = 2001 GROUP BY c.c_customer_id, c.c_first_name, c.c_last_name, c.c_birth_country), store_2002 AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, c.c_birth_country AS customer_birth_country, 2002 AS dyear, SUM(ss.ss_ext_list_price - ss.ss_ext_discount_amt) AS year_total, 's' AS sale_type FROM customer c, store_sales ss, date_dim d WHERE c.c_customer_sk = ss.ss_customer_sk AND ss.ss_sold_date_sk = d.d_date_sk AND d.d_year = 2002 GROUP BY c.c_customer_id, c.c_first_name, c.c_last_name, c.c_birth_country), web_2001 AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, c.c_birth_country AS customer_birth_country, 2001 AS dyear, SUM(ws.ws_ext_list_price - ws.ws_ext_discount_amt) AS year_total, 'w' AS sale_type FROM customer c, web_sales ws, date_dim d WHERE c.c_customer_sk = ws.ws_bill_customer_sk AND ws.ws_sold_date_sk = d.d_date_sk AND d.d_year = 2001 GROUP BY c.c_customer_id, c.c_first_name, c.c_last_name, c.c_birth_country), web_2002 AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, c.c_birth_country AS customer_birth_country, 2002 AS dyear, SUM(ws.ws_ext_list_price - ws.ws_ext_discount_amt) AS year_total, 'w' AS sale_type FROM customer c, web_sales ws, date_dim d WHERE c.c_customer_sk = ws.ws_bill_customer_sk AND ws.ws_sold_date_sk = d.d_date_sk AND d.d_year = 2002 GROUP BY c.c_customer_id, c.c_first_name, c.c_last_name, c.c_birth_country) SELECT s2.customer_id, s2.customer_first_name, s2.customer_last_name, s2.customer_birth_country FROM store_2001 s1, store_2002 s2, web_2001 w1, web_2002 w2 WHERE s1.customer_id = s2.customer_id AND s2.customer_id = w1.customer_id AND w1.customer_id = w2.customer_id AND s1.year_total > 0 AND w1.year_total > 0 AND (CASE WHEN w1.year_total > 0 THEN w2.year_total / w1.year_total ELSE 0.0 END) > (CASE WHEN s1.year_total > 0 THEN s2.year_total / s1.year_total ELSE 0.0 END) ORDER BY s2.customer_id, s2.customer_first_name, s2.customer_last_name, s2.customer_birth_country LIMIT 100