WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('1998-04-06' AS DATE) AND (CAST('1998-04-06' AS DATE) + INTERVAL 30 DAY)), prefetched_fact AS (SELECT ws_item_sk, ws_ext_sales_price FROM web_sales INNER JOIN filtered_dates ON ws_sold_date_sk = d_date_sk), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_category, i_class, i_current_price FROM item WHERE i_category IN ('Books', 'Sports', 'Men')), aggregated AS (SELECT i_item_id, i_item_desc, i_category, i_class, i_current_price, SUM(ws_ext_sales_price) AS itemrevenue FROM prefetched_fact INNER JOIN filtered_items ON ws_item_sk = i_item_sk GROUP BY i_item_id, i_item_desc, i_category, i_class, i_current_price), windowed AS (SELECT i_item_id, i_item_desc, i_category, i_class, i_current_price, itemrevenue, (itemrevenue * 100 / SUM(itemrevenue) OVER (PARTITION BY i_class)) AS revenueratio FROM aggregated), top_n AS (SELECT i_item_id, i_item_desc, i_category, i_class, i_current_price, itemrevenue, revenueratio FROM windowed ORDER BY i_category ASC, i_class ASC, i_item_id ASC, i_item_desc ASC, revenueratio ASC LIMIT 100) SELECT i_item_id, i_item_desc, i_category, i_class, i_current_price, itemrevenue, revenueratio FROM top_n