WITH filtered_dates_3year AS (SELECT d_date_sk FROM date_dim WHERE d_year BETWEEN 2000 AND 2000 + 2), filtered_dates_nov2002 AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000 + 2 AND d_moy = 11), cross_items AS (SELECT i_item_sk AS ss_item_sk FROM item, (SELECT iss.i_brand_id AS brand_id, iss.i_class_id AS class_id, iss.i_category_id AS category_id FROM store_sales JOIN item iss ON ss_item_sk = iss.i_item_sk JOIN filtered_dates_3year d1 ON ss_sold_date_sk = d1.d_date_sk INTERSECT SELECT ics.i_brand_id, ics.i_class_id, ics.i_category_id FROM catalog_sales JOIN item ics ON cs_item_sk = ics.i_item_sk JOIN filtered_dates_3year d2 ON cs_sold_date_sk = d2.d_date_sk INTERSECT SELECT iws.i_brand_id, iws.i_class_id, iws.i_category_id FROM web_sales JOIN item iws ON ws_item_sk = iws.i_item_sk JOIN filtered_dates_3year d3 ON ws_sold_date_sk = d3.d_date_sk) AS x WHERE i_brand_id = brand_id AND i_class_id = class_id AND i_category_id = category_id), prefetched_store_sales AS (SELECT ss_item_sk, ss_quantity, ss_list_price FROM store_sales JOIN filtered_dates_nov2002 ON ss_sold_date_sk = d_date_sk WHERE ss_item_sk IN (SELECT ss_item_sk FROM cross_items)), prefetched_catalog_sales AS (SELECT cs_item_sk, cs_quantity, cs_list_price FROM catalog_sales JOIN filtered_dates_nov2002 ON cs_sold_date_sk = d_date_sk WHERE cs_item_sk IN (SELECT ss_item_sk FROM cross_items)), prefetched_web_sales AS (SELECT ws_item_sk, ws_quantity, ws_list_price FROM web_sales JOIN filtered_dates_nov2002 ON ws_sold_date_sk = d_date_sk WHERE ws_item_sk IN (SELECT ss_item_sk FROM cross_items)), avg_sales AS (SELECT AVG(quantity * list_price) AS average_sales FROM (SELECT ss_quantity AS quantity, ss_list_price AS list_price FROM store_sales JOIN filtered_dates_3year ON ss_sold_date_sk = d_date_sk UNION ALL SELECT cs_quantity AS quantity, cs_list_price AS list_price FROM catalog_sales JOIN filtered_dates_3year ON cs_sold_date_sk = d_date_sk UNION ALL SELECT ws_quantity AS quantity, ws_list_price AS list_price FROM web_sales JOIN filtered_dates_3year ON ws_sold_date_sk = d_date_sk) AS x) SELECT channel, i_brand_id, i_class_id, i_category_id, SUM(sales) AS "sum(sales)", SUM(number_sales) AS "sum(number_sales)" FROM (SELECT 'store' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ss_quantity * ss_list_price) AS sales, COUNT(*) AS number_sales FROM prefetched_store_sales JOIN item ON ss_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ss_quantity * ss_list_price) > (SELECT average_sales FROM avg_sales) UNION ALL SELECT 'catalog' AS channel, i_brand_id, i_class_id, i_category_id, SUM(cs_quantity * cs_list_price) AS sales, COUNT(*) AS number_sales FROM prefetched_catalog_sales JOIN item ON cs_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(cs_quantity * cs_list_price) > (SELECT average_sales FROM avg_sales) UNION ALL SELECT 'web' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ws_quantity * ws_list_price) AS sales, COUNT(*) AS number_sales FROM prefetched_web_sales JOIN item ON ws_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ws_quantity * ws_list_price) > (SELECT average_sales FROM avg_sales)) AS y GROUP BY ROLLUP(channel, i_brand_id, i_class_id, i_category_id) ORDER BY channel, i_brand_id, i_class_id, i_category_id LIMIT 100