WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-4-01' AND (CAST('2002-4-01' AS DATE) + INTERVAL 60 DAY)), addr_cte AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'WV'), call_cte AS (SELECT cc_call_center_sk FROM call_center WHERE cc_county IN ('Ziebach County', 'Luce County', 'Richland County', 'Daviess County', 'Barrow County')), warehouse_bitmap_cte AS (SELECT cs_order_number FROM catalog_sales GROUP BY cs_order_number HAVING MIN(cs_warehouse_sk) <> MAX(cs_warehouse_sk)), filtered_fact AS (SELECT cs1.cs_order_number, cs1.cs_ext_ship_cost, cs1.cs_net_profit FROM catalog_sales cs1 INNER JOIN date_cte ON cs1.cs_ship_date_sk = date_cte.d_date_sk INNER JOIN addr_cte ON cs1.cs_ship_addr_sk = addr_cte.ca_address_sk INNER JOIN call_cte ON cs1.cs_call_center_sk = call_cte.cc_call_center_sk INNER JOIN warehouse_bitmap_cte w ON cs1.cs_order_number = w.cs_order_number), no_returns_filter AS (SELECT cs_order_number, cs_ext_ship_cost, cs_net_profit FROM filtered_fact WHERE NOT EXISTS (SELECT 1 FROM catalog_returns cr1 WHERE filtered_fact.cs_order_number = cr1.cr_order_number)) SELECT COUNT(DISTINCT cs_order_number) AS "order count", SUM(cs_ext_ship_cost) AS "total shipping cost", SUM(cs_net_profit) AS "total net profit" FROM no_returns_filter ORDER BY COUNT(DISTINCT cs_order_number) LIMIT 100