WITH d1_cte AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name = '2001Q1'), d2_cte AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), d3_cte AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), ss_prejoined AS (SELECT ss_item_sk, ss_store_sk, ss_customer_sk, ss_ticket_number, ss_quantity FROM store_sales JOIN d1_cte ON store_sales.ss_sold_date_sk = d1_cte.d_date_sk), sr_prejoined AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, sr_return_quantity FROM store_returns JOIN d2_cte ON store_returns.sr_returned_date_sk = d2_cte.d_date_sk), cs_prejoined AS (SELECT cs_item_sk, cs_bill_customer_sk, cs_quantity FROM catalog_sales JOIN d3_cte ON catalog_sales.cs_sold_date_sk = d3_cte.d_date_sk), main_join AS (SELECT i.i_item_id, i.i_item_desc, s.s_state, ss.ss_quantity, sr.sr_return_quantity, cs.cs_quantity FROM ss_prejoined ss JOIN sr_prejoined sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_ticket_number = sr.sr_ticket_number JOIN cs_prejoined cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk) SELECT i_item_id, i_item_desc, s_state, count(ss_quantity) AS store_sales_quantitycount, avg(ss_quantity) AS store_sales_quantityave, stddev_samp(ss_quantity) AS store_sales_quantitystdev, CASE WHEN avg(ss_quantity) > 0 THEN stddev_samp(ss_quantity) / avg(ss_quantity) ELSE NULL END AS store_sales_quantitycov, count(sr_return_quantity) AS store_returns_quantitycount, avg(sr_return_quantity) AS store_returns_quantityave, stddev_samp(sr_return_quantity) AS store_returns_quantitystdev, CASE WHEN avg(sr_return_quantity) > 0 THEN stddev_samp(sr_return_quantity) / avg(sr_return_quantity) ELSE NULL END AS store_returns_quantitycov, count(cs_quantity) AS catalog_sales_quantitycount, avg(cs_quantity) AS catalog_sales_quantityave, stddev_samp(cs_quantity) AS catalog_sales_quantitystdev, CASE WHEN avg(cs_quantity) > 0 THEN stddev_samp(cs_quantity) / avg(cs_quantity) ELSE NULL END AS catalog_sales_quantitycov FROM main_join GROUP BY i_item_id, i_item_desc, s_state ORDER BY i_item_id, i_item_desc, s_state LIMIT 100