WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), filtered_cd1 AS (SELECT cd_demo_sk, cd_dep_count FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'Advanced Degree'), filtered_customer_chain AS (SELECT c.c_customer_sk, c.c_birth_year, ca.ca_country, ca.ca_state, ca.ca_county FROM customer c INNER JOIN customer_demographics cd2 ON c.c_current_cdemo_sk = cd2.cd_demo_sk INNER JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE c.c_birth_month IN (10,7,8,4,1,2) AND ca.ca_state IN ('WA','GA','NC','ME','WY','OK','IN')), prefact AS (SELECT cs.cs_item_sk, cc.c_birth_year, cc.ca_country, cc.ca_state, cc.ca_county, cs.cs_quantity, cs.cs_list_price, cs.cs_coupon_amt, cs.cs_sales_price, cs.cs_net_profit, fcd1.cd_dep_count FROM catalog_sales cs INNER JOIN filtered_date fd ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_cd1 fcd1 ON cs.cs_bill_cdemo_sk = fcd1.cd_demo_sk INNER JOIN filtered_customer_chain cc ON cs.cs_bill_customer_sk = cc.c_customer_sk), fact_join_item AS (SELECT i.i_item_id, pf.ca_country, pf.ca_state, pf.ca_county, pf.cs_quantity, pf.cs_list_price, pf.cs_coupon_amt, pf.cs_sales_price, pf.cs_net_profit, pf.c_birth_year, pf.cd_dep_count FROM prefact pf INNER JOIN item i ON pf.cs_item_sk = i.i_item_sk), rollup_agg AS (SELECT i_item_id, ca_country, ca_state, ca_county, AVG(CAST(cs_quantity AS DECIMAL(12,2))) AS agg1, AVG(CAST(cs_list_price AS DECIMAL(12,2))) AS agg2, AVG(CAST(cs_coupon_amt AS DECIMAL(12,2))) AS agg3, AVG(CAST(cs_sales_price AS DECIMAL(12,2))) AS agg4, AVG(CAST(cs_net_profit AS DECIMAL(12,2))) AS agg5, AVG(CAST(c_birth_year AS DECIMAL(12,2))) AS agg6, AVG(CAST(cd_dep_count AS DECIMAL(12,2))) AS agg7 FROM fact_join_item GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county)) SELECT i_item_id, ca_country, ca_state, ca_county, agg1, agg2, agg3, agg4, agg5, agg6, agg7 FROM rollup_agg ORDER BY ca_country, ca_state, ca_county, i_item_id LIMIT 100