WITH date_item_fact AS (SELECT ss.ss_customer_sk, ss.ss_store_sk, ss.ss_ext_sales_price, i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact FROM date_dim d JOIN store_sales ss ON d.d_date_sk = ss.ss_sold_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE d.d_moy = 12 AND d.d_year = 1999 AND i.i_manager_id = 2), early_agg AS (SELECT i_brand_id, i_brand, i_manufact_id, i_manufact, ss_customer_sk, ss_store_sk, SUM(ss_ext_sales_price) AS ext_price FROM date_item_fact GROUP BY i_brand_id, i_brand, i_manufact_id, i_manufact, ss_customer_sk, ss_store_sk), late_join_customer_store AS (SELECT a.i_brand_id, a.i_brand, a.i_manufact_id, a.i_manufact, a.ext_price, ca.ca_zip, s.s_zip FROM early_agg a JOIN customer c ON a.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store s ON a.ss_store_sk = s.s_store_sk) SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ext_price) AS ext_price FROM late_join_customer_store WHERE substr(ca_zip, 1, 5) <> substr(s_zip, 1, 5) GROUP BY i_brand_id, i_brand, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100