WITH date_item_store_keys AS (SELECT d.d_date_sk, i.i_item_sk, s.s_store_sk, s.s_zip FROM date_dim d CROSS JOIN item i CROSS JOIN store s WHERE d.d_moy = 12 AND d.d_year = 1999 AND i.i_manager_id = 2 AND s.s_store_sk <= 100), fact_filtered AS (SELECT ss.ss_customer_sk, ss.ss_ext_sales_price, k.i_item_sk, k.s_store_sk, k.s_zip, k.d_date_sk FROM date_item_store_keys k JOIN store_sales ss ON k.d_date_sk = ss.ss_sold_date_sk AND k.i_item_sk = ss.ss_item_sk AND k.s_store_sk = ss.ss_store_sk), join_customer_addr AS (SELECT f.ss_ext_sales_price, f.i_item_sk, f.s_store_sk FROM fact_filtered f JOIN customer c ON f.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE substr(ca.ca_zip, 1, 5) <> substr(f.s_zip, 1, 5)), aggregation AS (SELECT i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact, SUM(j.ss_ext_sales_price) AS ext_price FROM join_customer_addr j JOIN item i ON j.i_item_sk = i.i_item_sk GROUP BY i.i_brand, i.i_brand_id, i.i_manufact_id, i.i_manufact) SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, ext_price FROM aggregation ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100