WITH date_cte AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN DATE '2002-02-27' - INTERVAL 30 DAY AND DATE '2002-02-27' + INTERVAL 30 DAY), item_cte AS (SELECT i_item_sk, i_item_id FROM item WHERE i_current_price BETWEEN 0.99 AND 1.49), warehouse_cte AS (SELECT w_warehouse_sk, w_warehouse_name FROM warehouse), inventory_join_all AS (SELECT w.w_warehouse_name, i.i_item_id, inv.inv_quantity_on_hand, d.d_date FROM inventory inv JOIN date_cte d ON inv.inv_date_sk = d.d_date_sk JOIN item_cte i ON inv.inv_item_sk = i.i_item_sk JOIN warehouse_cte w ON inv.inv_warehouse_sk = w.w_warehouse_sk), aggregate AS (SELECT w_warehouse_name, i_item_id, SUM(CASE WHEN d_date < DATE '2002-02-27' THEN inv_quantity_on_hand ELSE 0 END) AS inv_before, SUM(CASE WHEN d_date >= DATE '2002-02-27' THEN inv_quantity_on_hand ELSE 0 END) AS inv_after FROM inventory_join_all GROUP BY w_warehouse_name, i_item_id), ratio_filter AS (SELECT w_warehouse_name, i_item_id, inv_before, inv_after FROM aggregate WHERE (CASE WHEN inv_before > 0 THEN inv_after / inv_before ELSE NULL END) BETWEEN (2.0/3.0) AND (3.0/2.0)) SELECT * FROM ratio_filter ORDER BY w_warehouse_name ASC, i_item_id ASC LIMIT 100