WITH date_cte AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN DATE '2002-02-27' - INTERVAL 30 DAY AND DATE '2002-02-27' + INTERVAL 30 DAY), inventory_join_date AS (SELECT inv_warehouse_sk, inv_item_sk, inv_quantity_on_hand, d_date FROM inventory JOIN date_cte ON inv_date_sk = d_date_sk), item_join AS (SELECT inv_warehouse_sk, i_item_id, inv_quantity_on_hand, d_date FROM inventory_join_date JOIN item ON inv_item_sk = i_item_sk AND i_current_price BETWEEN 0.99 AND 1.49), warehouse_join AS (SELECT w_warehouse_name, i_item_id, inv_quantity_on_hand, d_date FROM item_join JOIN warehouse ON inv_warehouse_sk = w_warehouse_sk), aggregate AS (SELECT w_warehouse_name, i_item_id, SUM(CASE WHEN d_date < DATE '2002-02-27' THEN inv_quantity_on_hand ELSE 0 END) AS inv_before, SUM(CASE WHEN d_date >= DATE '2002-02-27' THEN inv_quantity_on_hand ELSE 0 END) AS inv_after FROM warehouse_join GROUP BY w_warehouse_name, i_item_id), ratio_filter AS (SELECT w_warehouse_name, i_item_id, inv_before, inv_after FROM aggregate WHERE (CASE WHEN inv_before > 0 THEN inv_after / inv_before ELSE NULL END) BETWEEN (2.0/3.0) AND (3.0/2.0)), order_limit AS (SELECT w_warehouse_name, i_item_id, inv_before, inv_after FROM ratio_filter ORDER BY w_warehouse_name ASC, i_item_id ASC LIMIT 100) SELECT * FROM order_limit