WITH filtered_store_sales_2000_2003 AS (SELECT ss_item_sk, ss_customer_sk, ss_quantity, ss_sales_price, d_date FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_year >= 2000 AND d_year <= 2003), frequent_items_agg AS (SELECT substr(i_item_desc,1,30) AS itemdesc, i_item_sk AS item_sk, d_date AS solddate, COUNT(*) AS cnt FROM filtered_store_sales_2000_2003 JOIN item ON ss_item_sk = i_item_sk GROUP BY substr(i_item_desc,1,30), i_item_sk, d_date HAVING COUNT(*) > 4), max_sales_agg AS (SELECT c_customer_sk, SUM(ss_quantity * ss_sales_price) AS csales FROM filtered_store_sales_2000_2003 JOIN customer ON ss_customer_sk = c_customer_sk GROUP BY c_customer_sk), max_store_sales_cte AS (SELECT MAX(csales) AS tpcds_cmax FROM max_sales_agg), best_customers AS (SELECT c_customer_sk, SUM(ss_quantity * ss_sales_price) AS ssales FROM store_sales JOIN customer ON ss_customer_sk = c_customer_sk GROUP BY c_customer_sk HAVING SUM(ss_quantity * ss_sales_price) > (95/100.0) * (SELECT tpcds_cmax FROM max_store_sales_cte)) SELECT SUM(sales) FROM (SELECT cs_quantity * cs_list_price AS sales FROM catalog_sales, date_dim WHERE d_year = 2000 AND d_moy = 5 AND cs_sold_date_sk = d_date_sk AND cs_item_sk IN (SELECT item_sk FROM frequent_ss_items) AND cs_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer) UNION ALL SELECT ws_quantity * ws_list_price AS sales FROM web_sales, date_dim WHERE d_year = 2000 AND d_moy = 5 AND ws_sold_date_sk = d_date_sk AND ws_item_sk IN (SELECT item_sk FROM frequent_ss_items) AND ws_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer)) LIMIT 100