WITH date_filter_2000_2003 AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year IN (2000, 2000+1, 2000+2, 2000+3)), store_sales_filtered AS (SELECT ss_item_sk, ss_customer_sk, ss_quantity, ss_sales_price, d_date FROM store_sales JOIN date_filter_2000_2003 ON ss_sold_date_sk = d_date_sk), frequent_items_cte AS (SELECT substr(i_item_desc,1,30) AS itemdesc, i_item_sk AS item_sk, d_date AS solddate, COUNT(*) AS cnt FROM store_sales_filtered JOIN item ON ss_item_sk = i_item_sk GROUP BY substr(i_item_desc,1,30), i_item_sk, d_date HAVING COUNT(*) > 4), max_store_sales_cte AS (SELECT c_customer_sk, SUM(ss_quantity * ss_sales_price) AS csales FROM store_sales_filtered JOIN customer ON ss_customer_sk = c_customer_sk GROUP BY c_customer_sk), max_store_sales_agg AS (SELECT MAX(csales) AS tpcds_cmax FROM max_store_sales_cte), best_customers AS (SELECT c_customer_sk, SUM(ss_quantity * ss_sales_price) AS ssales FROM store_sales JOIN customer ON ss_customer_sk = c_customer_sk GROUP BY c_customer_sk HAVING SUM(ss_quantity * ss_sales_price) > (95/100.0) * (SELECT tpcds_cmax FROM max_store_sales_agg)) SELECT SUM(sales) FROM (SELECT cs_quantity * cs_list_price AS sales FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE d_year = 2000 AND d_moy = 5 AND cs_item_sk IN (SELECT item_sk FROM frequent_items_cte) AND cs_bill_customer_sk IN (SELECT c_customer_sk FROM best_customers) UNION ALL SELECT ws_quantity * ws_list_price AS sales FROM web_sales JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE d_year = 2000 AND d_moy = 5 AND ws_item_sk IN (SELECT item_sk FROM frequent_items_cte) AND ws_bill_customer_sk IN (SELECT c_customer_sk FROM best_customers))