WITH store_sales_single_scan AS (SELECT ss_item_sk, i_item_desc, ss_customer_sk, ss_quantity, ss_sales_price, d_date FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk AND d_year IN (2000, 2001, 2002, 2003) JOIN item ON ss_item_sk = i_item_sk JOIN customer ON ss_customer_sk = c_customer_sk), frequent_ss_items_and_customer_aggs AS (SELECT SUBSTR(i_item_desc,1,30) AS itemdesc, ss_item_sk AS item_sk, d_date AS solddate, COUNT(*) AS cnt, ss_customer_sk, SUM(ss_quantity * ss_sales_price) AS customer_daily_sales FROM store_sales_single_scan GROUP BY SUBSTR(i_item_desc,1,30), ss_item_sk, d_date, ss_customer_sk), frequent_ss_items_filtered AS (SELECT itemdesc, item_sk, solddate, cnt FROM frequent_ss_items_and_customer_aggs WHERE cnt > 4 GROUP BY itemdesc, item_sk, solddate, cnt), customer_aggs AS (SELECT ss_customer_sk, SUM(customer_daily_sales) AS total_sales FROM frequent_ss_items_and_customer_aggs GROUP BY ss_customer_sk), max_store_sales AS (SELECT MAX(total_sales) AS tpcds_cmax FROM customer_aggs), best_ss_customer AS (SELECT ss_customer_sk AS c_customer_sk, total_sales AS ssales FROM customer_aggs WHERE total_sales > (95/100.0) * (SELECT tpcds_cmax FROM max_store_sales)) SELECT c_last_name, c_first_name, SUM(sales) AS sales FROM (SELECT c_last_name, c_first_name, SUM(cs_quantity * cs_list_price) AS sales FROM catalog_sales, customer, date_dim WHERE d_year = 2000 AND d_moy = 5 AND cs_sold_date_sk = d_date_sk AND cs_item_sk IN (SELECT item_sk FROM frequent_ss_items_filtered) AND cs_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer) AND cs_bill_customer_sk = c_customer_sk GROUP BY c_last_name, c_first_name UNION ALL SELECT c_last_name, c_first_name, SUM(ws_quantity * ws_list_price) AS sales FROM web_sales, customer, date_dim WHERE d_year = 2000 AND d_moy = 5 AND ws_sold_date_sk = d_date_sk AND ws_item_sk IN (SELECT item_sk FROM frequent_ss_items_filtered) AND ws_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer) AND ws_bill_customer_sk = c_customer_sk GROUP BY c_last_name, c_first_name) AS combined GROUP BY c_last_name, c_first_name ORDER BY c_last_name, c_first_name, sales LIMIT 100