WITH d1_cte AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 4 AND d_year = 1999), store_sales_pre AS (SELECT ss_item_sk, ss_customer_sk, ss_ticket_number, ss_store_sk, ss_quantity FROM store_sales JOIN d1_cte ON ss_sold_date_sk = d_date_sk), d2_cte AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 4 AND 7 AND d_year = 1999), store_returns_pre AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, sr_return_quantity FROM store_returns JOIN d2_cte ON sr_returned_date_sk = d_date_sk), d3_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001)), catalog_sales_pre AS (SELECT cs_item_sk, cs_bill_customer_sk, cs_quantity FROM catalog_sales JOIN d3_cte ON cs_sold_date_sk = d_date_sk), combined_facts AS (SELECT ss.ss_item_sk, ss.ss_store_sk, ss.ss_quantity, sr.sr_return_quantity, cs.cs_quantity FROM store_sales_pre ss JOIN store_returns_pre sr ON ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number JOIN catalog_sales_pre cs ON sr.sr_customer_sk = cs.cs_bill_customer_sk AND sr.sr_item_sk = cs.cs_item_sk), store_item_join AS (SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, cf.ss_quantity, cf.sr_return_quantity, cf.cs_quantity FROM combined_facts cf JOIN store s ON cf.ss_store_sk = s.s_store_sk JOIN item i ON cf.ss_item_sk = i.i_item_sk), aggregate AS (SELECT i_item_id, i_item_desc, s_store_id, s_store_name, AVG(ss_quantity) AS store_sales_quantity, AVG(sr_return_quantity) AS store_returns_quantity, AVG(cs_quantity) AS catalog_sales_quantity FROM store_item_join GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name), order_limit AS (SELECT i_item_id, i_item_desc, s_store_id, s_store_name, store_sales_quantity, store_returns_quantity, catalog_sales_quantity FROM aggregate ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, store_sales_quantity, store_returns_quantity, catalog_sales_quantity FROM order_limit