WITH filtered_date AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_moy = 11), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand FROM item WHERE i_manufact_id = 816), joined_sales AS (SELECT d_year, i_brand, i_brand_id, SUM(ss_sales_price) AS sum_agg FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_item ON store_sales.ss_item_sk = filtered_item.i_item_sk GROUP BY d_year, i_brand, i_brand_id) SELECT d_year, i_brand_id AS brand_id, i_brand AS brand, sum_agg FROM joined_sales ORDER BY d_year ASC, sum_agg DESC, i_brand_id ASC LIMIT 100