WITH explicit_joins AS (SELECT store_sales.ss_sales_price, date_dim.d_year, item.i_brand_id, item.i_brand FROM store_sales INNER JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk INNER JOIN item ON store_sales.ss_item_sk = item.i_item_sk WHERE item.i_manufact_id = 816 AND date_dim.d_moy = 11), group_agg AS (SELECT d_year, i_brand_id, i_brand, SUM(ss_sales_price) AS sum_agg FROM explicit_joins GROUP BY d_year, i_brand, i_brand_id) SELECT d_year, i_brand_id AS brand_id, i_brand AS brand, sum_agg FROM group_agg ORDER BY d_year ASC, sum_agg DESC, i_brand_id ASC LIMIT 100