WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN DATE '1999-01-07' AND (DATE '1999-01-07' + INTERVAL 90 DAY)), filtered_items AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 29), consolidated_sales AS (SELECT cs.cs_item_sk, cs.cs_sold_date_sk, cs.cs_ext_discount_amt, AVG(cs.cs_ext_discount_amt) OVER (PARTITION BY cs.cs_item_sk) AS item_avg_discount FROM catalog_sales cs INNER JOIN filtered_dates d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN filtered_items i ON cs.cs_item_sk = i.i_item_sk), final_aggregation AS (SELECT SUM(cs_ext_discount_amt) AS "excess discount amount" FROM consolidated_sales WHERE cs_ext_discount_amt > 1.3 * item_avg_discount) SELECT "excess discount amount" FROM final_aggregation LIMIT 100