WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 1), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category = 'Home'), store_sales_join AS (SELECT filtered_item.i_manufact_id, ss_ext_sales_price AS sales_price FROM store_sales INNER JOIN filtered_date ON ss_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_address ON ss_addr_sk = filtered_address.ca_address_sk INNER JOIN filtered_item ON ss_item_sk = filtered_item.i_item_sk), catalog_sales_join AS (SELECT filtered_item.i_manufact_id, cs_ext_sales_price AS sales_price FROM catalog_sales INNER JOIN filtered_date ON cs_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_address ON cs_bill_addr_sk = filtered_address.ca_address_sk INNER JOIN filtered_item ON cs_item_sk = filtered_item.i_item_sk), web_sales_join AS (SELECT filtered_item.i_manufact_id, ws_ext_sales_price AS sales_price FROM web_sales INNER JOIN filtered_date ON ws_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_address ON ws_bill_addr_sk = filtered_address.ca_address_sk INNER JOIN filtered_item ON ws_item_sk = filtered_item.i_item_sk), union_all AS (SELECT i_manufact_id, sales_price FROM store_sales_join UNION ALL SELECT i_manufact_id, sales_price FROM catalog_sales_join UNION ALL SELECT i_manufact_id, sales_price FROM web_sales_join) SELECT i_manufact_id, SUM(sales_price) AS total_sales FROM union_all GROUP BY i_manufact_id ORDER BY total_sales ASC LIMIT 100