WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 1), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category = 'Home'), ss_agg AS (SELECT i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales INNER JOIN filtered_date ON ss_sold_date_sk = d_date_sk INNER JOIN filtered_address ON ss_addr_sk = ca_address_sk INNER JOIN filtered_item ON ss_item_sk = i_item_sk GROUP BY i_manufact_id), cs_agg AS (SELECT i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales INNER JOIN filtered_date ON cs_sold_date_sk = d_date_sk INNER JOIN filtered_address ON cs_bill_addr_sk = ca_address_sk INNER JOIN filtered_item ON cs_item_sk = i_item_sk GROUP BY i_manufact_id), ws_agg AS (SELECT i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales INNER JOIN filtered_date ON ws_sold_date_sk = d_date_sk INNER JOIN filtered_address ON ws_bill_addr_sk = ca_address_sk INNER JOIN filtered_item ON ws_item_sk = i_item_sk GROUP BY i_manufact_id), union_all AS (SELECT i_manufact_id, total_sales FROM ss_agg UNION ALL SELECT i_manufact_id, total_sales FROM cs_agg UNION ALL SELECT i_manufact_id, total_sales FROM ws_agg) SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM union_all GROUP BY i_manufact_id ORDER BY total_sales ASC LIMIT 100