WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 1), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category = 'Home'), store_sales_join AS (SELECT fi.i_manufact_id, ss.ss_ext_sales_price AS sales_price FROM store_sales ss INNER JOIN filtered_date fd ON ss.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_address fa ON ss.ss_addr_sk = fa.ca_address_sk INNER JOIN filtered_item fi ON ss.ss_item_sk = fi.i_item_sk), catalog_sales_join AS (SELECT fi.i_manufact_id, cs.cs_ext_sales_price AS sales_price FROM catalog_sales cs INNER JOIN filtered_date fd ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_address fa ON cs.cs_bill_addr_sk = fa.ca_address_sk INNER JOIN filtered_item fi ON cs.cs_item_sk = fi.i_item_sk), web_sales_join AS (SELECT fi.i_manufact_id, ws.ws_ext_sales_price AS sales_price FROM web_sales ws INNER JOIN filtered_date fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_address fa ON ws.ws_bill_addr_sk = fa.ca_address_sk INNER JOIN filtered_item fi ON ws.ws_item_sk = fi.i_item_sk), union_all_sales AS (SELECT i_manufact_id, sales_price FROM store_sales_join UNION ALL SELECT i_manufact_id, sales_price FROM catalog_sales_join UNION ALL SELECT i_manufact_id, sales_price FROM web_sales_join) SELECT i_manufact_id, SUM(sales_price) AS total_sales FROM union_all_sales GROUP BY i_manufact_id ORDER BY total_sales ASC LIMIT 100