WITH date_filtered AS (SELECT d_date_sk FROM date_dim WHERE (d_dom BETWEEN 1 AND 3 OR d_dom BETWEEN 25 AND 28) AND d_year IN (1998, 1999, 2000)), store_filtered AS (SELECT s_store_sk FROM store WHERE s_county IN ('Ziebach County', 'Daviess County', 'Walker County', 'Richland County', 'Barrow County', 'Franklin Parish', 'Williamson County', 'Luce County')), hh_filtered AS (SELECT hd_demo_sk FROM household_demographics WHERE (hd_buy_potential = '1001-5000' OR hd_buy_potential = '0-500') AND hd_vehicle_count > 0 AND (CASE WHEN hd_vehicle_count > 0 THEN hd_dep_count / hd_vehicle_count ELSE NULL END) > 1.2), fact_join AS (SELECT ss_ticket_number, ss_customer_sk FROM store_sales INNER JOIN date_filtered ON ss_sold_date_sk = d_date_sk INNER JOIN store_filtered ON ss_store_sk = s_store_sk INNER JOIN hh_filtered ON ss_hdemo_sk = hd_demo_sk), aggregation AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM fact_join GROUP BY ss_ticket_number, ss_customer_sk), customer_join AS (SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM aggregation INNER JOIN customer ON ss_customer_sk = c_customer_sk WHERE cnt BETWEEN 15 AND 20) SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM customer_join ORDER BY c_last_name, c_first_name, c_salutation, c_preferred_cust_flag DESC, ss_ticket_number