WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), filtered_store AS (SELECT s_store_sk FROM store WHERE s_state IN ('SD','TN','GA','SC','MO','AL','MI','OH')), base_join AS (SELECT i.i_category, i.i_class, ss.ss_net_profit, ss.ss_ext_sales_price FROM store_sales ss INNER JOIN filtered_date fd ON ss.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_store fs ON ss.ss_store_sk = fs.s_store_sk INNER JOIN item i ON ss.ss_item_sk = i.i_item_sk), level1_agg AS (SELECT i_category, i_class, 0 as lochierarchy, SUM(ss_net_profit) as sum_net_profit, SUM(ss_ext_sales_price) as sum_sales_price FROM base_join GROUP BY i_category, i_class), level2_agg AS (SELECT i_category, NULL as i_class, 1 as lochierarchy, SUM(ss_net_profit) as sum_net_profit, SUM(ss_ext_sales_price) as sum_sales_price FROM base_join GROUP BY i_category), level3_agg AS (SELECT NULL as i_category, NULL as i_class, 2 as lochierarchy, SUM(ss_net_profit) as sum_net_profit, SUM(ss_ext_sales_price) as sum_sales_price FROM base_join), union_all AS (SELECT * FROM level1_agg UNION ALL SELECT * FROM level2_agg UNION ALL SELECT * FROM level3_agg) SELECT (sum_net_profit / sum_sales_price) as gross_margin, i_category, i_class, lochierarchy, RANK() OVER (PARTITION BY lochierarchy, CASE WHEN lochierarchy = 0 THEN i_category END ORDER BY (sum_net_profit / sum_sales_price) ASC) as rank_within_parent FROM union_all ORDER BY lochierarchy DESC, CASE WHEN lochierarchy = 0 THEN i_category END, rank_within_parent LIMIT 100