WITH filtered_item AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 45 AND 45 + 30 AND i_manufact_id IN (856, 707, 1000, 747)), filtered_inventory_date_with_catalog AS (SELECT fi.i_item_sk, fi.i_item_id, fi.i_item_desc, fi.i_current_price, cs.cs_item_sk FROM inventory inv INNER JOIN filtered_item fi ON inv.inv_item_sk = fi.i_item_sk INNER JOIN date_dim d ON inv.inv_date_sk = d.d_date_sk INNER JOIN catalog_sales cs ON cs.cs_item_sk = fi.i_item_sk WHERE inv.inv_quantity_on_hand BETWEEN 100 AND 500 AND d.d_date BETWEEN CAST('1999-02-21' AS DATE) AND (CAST('1999-02-21' AS DATE) + INTERVAL 60 DAY)), early_group AS (SELECT i_item_id, i_item_desc, i_current_price FROM filtered_inventory_date_with_catalog GROUP BY i_item_id, i_item_desc, i_current_price) SELECT i_item_id, i_item_desc, i_current_price FROM early_group ORDER BY i_item_id LIMIT 100