WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1183 AND 1183 + 11), web_distinct AS (SELECT DISTINCT customer.c_last_name, customer.c_first_name, filtered_dates.d_date FROM web_sales INNER JOIN filtered_dates ON web_sales.ws_sold_date_sk = filtered_dates.d_date_sk INNER JOIN customer ON web_sales.ws_bill_customer_sk = customer.c_customer_sk) SELECT COUNT(*) FROM web_distinct wd WHERE EXISTS (SELECT 1 FROM catalog_sales INNER JOIN filtered_dates fd1 ON catalog_sales.cs_sold_date_sk = fd1.d_date_sk INNER JOIN customer cust1 ON catalog_sales.cs_bill_customer_sk = cust1.c_customer_sk WHERE cust1.c_last_name = wd.c_last_name AND cust1.c_first_name = wd.c_first_name AND fd1.d_date = wd.d_date) AND EXISTS (SELECT 1 FROM store_sales INNER JOIN filtered_dates fd2 ON store_sales.ss_sold_date_sk = fd2.d_date_sk INNER JOIN customer cust2 ON store_sales.ss_customer_sk = cust2.c_customer_sk WHERE cust2.c_last_name = wd.c_last_name AND cust2.c_first_name = wd.c_first_name AND fd2.d_date = wd.d_date) LIMIT 100