WITH filtered_date AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2001-04-02' AS DATE) - INTERVAL 30 DAY) AND (CAST('2001-04-02' AS DATE) + INTERVAL 30 DAY)), filtered_item AS (SELECT i_item_sk, i_item_id FROM item WHERE i_current_price BETWEEN 0.99 AND 1.49), filtered_warehouse AS (SELECT w_warehouse_sk, w_state FROM warehouse), sales_with_all_dims AS (SELECT cs.cs_order_number, cs.cs_item_sk, cs.cs_sales_price, fw.w_state, fi.i_item_id, fd.d_date FROM catalog_sales cs INNER JOIN filtered_date fd ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_item fi ON cs.cs_item_sk = fi.i_item_sk INNER JOIN filtered_warehouse fw ON cs.cs_warehouse_sk = fw.w_warehouse_sk), sales_with_returns AS (SELECT swd.w_state, swd.i_item_id, swd.cs_sales_price, COALESCE(cr.cr_refunded_cash, 0) AS cr_refunded_cash, swd.d_date FROM sales_with_all_dims swd LEFT JOIN catalog_returns cr ON swd.cs_order_number = cr.cr_order_number AND swd.cs_item_sk = cr.cr_item_sk) SELECT w_state, i_item_id, SUM(CASE WHEN d_date < CAST('2001-04-02' AS DATE) THEN cs_sales_price - cr_refunded_cash ELSE 0 END) AS sales_before, SUM(CASE WHEN d_date >= CAST('2001-04-02' AS DATE) THEN cs_sales_price - cr_refunded_cash ELSE 0 END) AS sales_after FROM sales_with_returns GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100