WITH filtered_date AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2001-04-02' AS DATE) - INTERVAL 30 DAY) AND (CAST('2001-04-02' AS DATE) + INTERVAL 30 DAY)), filtered_item AS (SELECT i_item_sk, i_item_id FROM item WHERE i_current_price BETWEEN 0.99 AND 1.49), returns_with_date AS (SELECT cr_order_number, cr_item_sk, cr_refunded_cash FROM catalog_returns INNER JOIN filtered_date ON cr_returned_date_sk = d_date_sk), sales_with_filtered_returns AS (SELECT w.w_state, i.i_item_id, d.d_date, cs.cs_sales_price, COALESCE(cr.cr_refunded_cash, 0) AS cr_refunded_cash FROM catalog_sales cs INNER JOIN filtered_date d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN filtered_item i ON cs.cs_item_sk = i.i_item_sk LEFT JOIN returns_with_date cr ON cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk INNER JOIN warehouse w ON cs.cs_warehouse_sk = w.w_warehouse_sk) SELECT w_state, i_item_id, SUM(CASE WHEN d_date < CAST('2001-04-02' AS DATE) THEN cs_sales_price - cr_refunded_cash ELSE 0 END) AS sales_before, SUM(CASE WHEN d_date >= CAST('2001-04-02' AS DATE) THEN cs_sales_price - cr_refunded_cash ELSE 0 END) AS sales_after FROM sales_with_filtered_returns GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100