WITH filtered_date AS (SELECT d_date_sk, d_day_name FROM date_dim WHERE d_year = 2000), filtered_store AS (SELECT s_store_sk, s_store_name, s_store_id FROM store WHERE s_gmt_offset = -5), prefetched_sales AS (SELECT st.s_store_name, st.s_store_id, dt.d_day_name, ss.ss_sales_price FROM store_sales ss INNER JOIN filtered_date dt ON ss.ss_sold_date_sk = dt.d_date_sk INNER JOIN filtered_store st ON ss.ss_store_sk = st.s_store_sk) SELECT s_store_name, s_store_id, SUM(CASE WHEN d_day_name='Sunday' THEN ss_sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN d_day_name='Monday' THEN ss_sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN d_day_name='Tuesday' THEN ss_sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN d_day_name='Wednesday' THEN ss_sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN d_day_name='Thursday' THEN ss_sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN d_day_name='Friday' THEN ss_sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN d_day_name='Saturday' THEN ss_sales_price ELSE NULL END) AS sat_sales FROM prefetched_sales GROUP BY s_store_name, s_store_id ORDER BY s_store_name, s_store_id, sun_sales, mon_sales, tue_sales, wed_sales, thu_sales, fri_sales, sat_sales LIMIT 100