WITH d2_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 8), returns_filtered AS (SELECT sr_ticket_number, sr_item_sk, sr_customer_sk, sr_returned_date_sk FROM store_returns JOIN d2_filtered ON sr_returned_date_sk = d2_filtered.d_date_sk), d1_all AS (SELECT d_date_sk FROM date_dim), store_all AS (SELECT s_store_sk, s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip FROM store), sales_joined AS (SELECT s.s_store_name, s.s_company_id, s.s_street_number, s.s_street_name, s.s_street_type, s.s_suite_number, s.s_city, s.s_county, s.s_state, s.s_zip, ss.ss_sold_date_sk, rf.sr_returned_date_sk FROM store_sales ss JOIN returns_filtered rf ON ss.ss_ticket_number = rf.sr_ticket_number AND ss.ss_item_sk = rf.sr_item_sk AND ss.ss_customer_sk = rf.sr_customer_sk JOIN d1_all d1 ON ss.ss_sold_date_sk = d1.d_date_sk JOIN store_all s ON ss.ss_store_sk = s.s_store_sk), aggregation AS (SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk <= 30) THEN 1 ELSE 0 END) AS "30 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 30 AND sr_returned_date_sk - ss_sold_date_sk <= 60) THEN 1 ELSE 0 END) AS "31-60 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 60 AND sr_returned_date_sk - ss_sold_date_sk <= 90) THEN 1 ELSE 0 END) AS "61-90 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 90 AND sr_returned_date_sk - ss_sold_date_sk <= 120) THEN 1 ELSE 0 END) AS "91-120 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 120) THEN 1 ELSE 0 END) AS ">120 days" FROM sales_joined GROUP BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip) SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, "30 days", "31-60 days", "61-90 days", "91-120 days", ">120 days" FROM aggregation ORDER BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip LIMIT 100