WITH filtered_date AS (SELECT dt.d_date_sk FROM date_dim dt WHERE dt.d_moy = 12 AND dt.d_year = 2002), date_filtered_sales AS (SELECT store_sales.ss_item_sk, store_sales.ss_ext_sales_price FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk), item_joined AS (SELECT item.i_brand_id, item.i_brand, date_filtered_sales.ss_ext_sales_price FROM date_filtered_sales INNER JOIN item ON date_filtered_sales.ss_item_sk = item.i_item_sk WHERE item.i_manager_id = 1), aggregated AS (SELECT 2002 AS d_year, i_brand_id AS brand_id, i_brand AS brand, SUM(ss_ext_sales_price) AS ext_price FROM item_joined GROUP BY i_brand, i_brand_id) SELECT d_year, brand_id, brand, ext_price FROM aggregated ORDER BY d_year ASC, ext_price DESC, brand_id ASC LIMIT 100