WITH filtered_date AS (SELECT dt.d_date_sk, dt.d_year FROM date_dim dt WHERE dt.d_moy = 12 AND dt.d_year = 2002), date_joined AS (SELECT filtered_date.d_year, store_sales.ss_item_sk, store_sales.ss_ext_sales_price FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk), item_filtered AS (SELECT date_joined.d_year, item.i_brand_id, item.i_brand, date_joined.ss_ext_sales_price FROM date_joined INNER JOIN item ON date_joined.ss_item_sk = item.i_item_sk WHERE item.i_manager_id = 1), aggregated AS (SELECT d_year, i_brand_id AS brand_id, i_brand AS brand, SUM(ss_ext_sales_price) AS ext_price FROM item_filtered GROUP BY d_year, i_brand, i_brand_id) SELECT d_year, brand_id, brand, ext_price FROM aggregated ORDER BY d_year ASC, ext_price DESC, brand_id ASC LIMIT 100