WITH date_range AS (SELECT MIN(d_date_sk) AS min_sk, MAX(d_date_sk) AS max_sk FROM date_dim WHERE d_moy = 12 AND d_year = 2002), range_filtered_sales AS (SELECT ss_item_sk, ss_ext_sales_price FROM store_sales WHERE ss_sold_date_sk BETWEEN (SELECT min_sk FROM date_range) AND (SELECT max_sk FROM date_range)), item_filtered AS (SELECT item.i_brand_id, item.i_brand, range_filtered_sales.ss_ext_sales_price FROM range_filtered_sales INNER JOIN item ON range_filtered_sales.ss_item_sk = item.i_item_sk WHERE item.i_manager_id = 1), aggregated AS (SELECT 2002 AS d_year, i_brand_id AS brand_id, i_brand AS brand, SUM(ss_ext_sales_price) AS ext_price FROM item_filtered GROUP BY i_brand, i_brand_id) SELECT d_year, brand_id, brand, ext_price FROM aggregated ORDER BY d_year ASC, ext_price DESC, brand_id ASC LIMIT 100