WITH date_range AS (SELECT DISTINCT d_month_seq FROM date_dim WHERE d_year = 1998 AND d_moy = 5), store_sales_with_date AS (SELECT ss_customer_sk, ss_ext_sales_price FROM store_sales JOIN date_dim d ON ss_sold_date_sk = d.d_date_sk WHERE d.d_month_seq BETWEEN (SELECT DISTINCT d_month_seq + 1 FROM date_range) AND (SELECT DISTINCT d_month_seq + 3 FROM date_range)), my_customers AS (SELECT DISTINCT c_customer_sk, c_current_addr_sk FROM (SELECT cs_sold_date_sk AS sold_date_sk, cs_bill_customer_sk AS customer_sk, cs_item_sk AS item_sk FROM catalog_sales UNION ALL SELECT ws_sold_date_sk AS sold_date_sk, ws_bill_customer_sk AS customer_sk, ws_item_sk AS item_sk FROM web_sales) AS cs_or_ws_sales, item, date_dim, customer WHERE sold_date_sk = d_date_sk AND item_sk = i_item_sk AND i_category = 'Women' AND i_class = 'maternity' AND c_customer_sk = cs_or_ws_sales.customer_sk AND d_moy = 5 AND d_year = 1998), my_revenue AS (SELECT c_customer_sk, SUM(ss_ext_sales_price) AS revenue FROM my_customers JOIN store_sales_with_date ON c_customer_sk = ss_customer_sk JOIN customer_address ON c_current_addr_sk = ca_address_sk JOIN store ON ca_county = s_county AND ca_state = s_state GROUP BY c_customer_sk), segments AS (SELECT CAST((revenue/50) AS INTEGER) AS segment FROM my_revenue) SELECT segment, COUNT(*) AS num_customers, segment * 50 AS segment_base FROM segments GROUP BY segment ORDER BY segment, num_customers LIMIT 100