WITH date_range AS (SELECT d_month_seq FROM date_dim WHERE d_year = 1998 AND d_moy = 5), store_sales_with_date AS (SELECT ss_customer_sk, ss_ext_sales_price, ss_sold_date_sk FROM store_sales INNER JOIN date_dim d ON store_sales.ss_sold_date_sk = d.d_date_sk WHERE d.d_month_seq BETWEEN (SELECT d_month_seq + 1 FROM date_range) AND (SELECT d_month_seq + 3 FROM date_range)), my_customers AS (SELECT DISTINCT c_customer_sk, c_current_addr_sk FROM (SELECT cs_sold_date_sk AS sold_date_sk, cs_bill_customer_sk AS customer_sk, cs_item_sk AS item_sk FROM catalog_sales UNION ALL SELECT ws_sold_date_sk AS sold_date_sk, ws_bill_customer_sk AS customer_sk, ws_item_sk AS item_sk FROM web_sales) cs_or_ws_sales INNER JOIN item ON cs_or_ws_sales.item_sk = item.i_item_sk INNER JOIN date_dim ON cs_or_ws_sales.sold_date_sk = date_dim.d_date_sk INNER JOIN customer ON cs_or_ws_sales.customer_sk = customer.c_customer_sk WHERE item.i_category = 'Women' AND item.i_class = 'maternity' AND date_dim.d_moy = 5 AND date_dim.d_year = 1998), my_revenue AS (SELECT my_customers.c_customer_sk, SUM(store_sales_with_date.ss_ext_sales_price) AS revenue FROM my_customers INNER JOIN store_sales_with_date ON my_customers.c_customer_sk = store_sales_with_date.ss_customer_sk INNER JOIN customer_address ON my_customers.c_current_addr_sk = customer_address.ca_address_sk INNER JOIN store ON (customer_address.ca_county = store.s_county AND customer_address.ca_state = store.s_state) GROUP BY my_customers.c_customer_sk), segments AS (SELECT CAST((revenue / 50) AS INT) AS segment FROM my_revenue) SELECT segment, COUNT(*) AS num_customers, segment * 50 AS segment_base FROM segments GROUP BY segment ORDER BY segment, num_customers LIMIT 100