WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000 AND d_moy = 2), filtered_customer AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -6), filtered_item AS (SELECT i_item_sk, i_item_id FROM item WHERE i_color IN ('powder', 'green', 'cyan')), ss_filtered AS (SELECT ss.ss_item_sk, ss.ss_ext_sales_price FROM store_sales ss INNER JOIN filtered_date fd ON ss.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customer fc ON ss.ss_addr_sk = fc.ca_address_sk), ss_with_item AS (SELECT fi.i_item_id, sf.ss_ext_sales_price FROM ss_filtered sf INNER JOIN filtered_item fi ON sf.ss_item_sk = fi.i_item_sk), ss_agg AS (SELECT i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM ss_with_item GROUP BY i_item_id), cs_filtered AS (SELECT cs.cs_item_sk, cs.cs_ext_sales_price FROM catalog_sales cs INNER JOIN filtered_date fd ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customer fc ON cs.cs_bill_addr_sk = fc.ca_address_sk), cs_with_item AS (SELECT fi.i_item_id, cf.cs_ext_sales_price FROM cs_filtered cf INNER JOIN filtered_item fi ON cf.cs_item_sk = fi.i_item_sk), cs_agg AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM cs_with_item GROUP BY i_item_id), ws_filtered AS (SELECT ws.ws_item_sk, ws.ws_ext_sales_price FROM web_sales ws INNER JOIN filtered_date fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customer fc ON ws.ws_bill_addr_sk = fc.ca_address_sk), ws_with_item AS (SELECT fi.i_item_id, wf.ws_ext_sales_price FROM ws_filtered wf INNER JOIN filtered_item fi ON wf.ws_item_sk = fi.i_item_sk), ws_agg AS (SELECT i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM ws_with_item GROUP BY i_item_id), union_all AS (SELECT i_item_id, total_sales FROM ss_agg UNION ALL SELECT i_item_id, total_sales FROM cs_agg UNION ALL SELECT i_item_id, total_sales FROM ws_agg) SELECT i_item_id, SUM(total_sales) AS total_sales FROM union_all GROUP BY i_item_id ORDER BY total_sales, i_item_id LIMIT 100