WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), customer_demo_cte AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'F' AND cd_marital_status = 'W' AND cd_education_status = 'College'), promotion_cte AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'N' OR p_channel_event = 'N'), filtered_sales AS (SELECT ss_item_sk, ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price FROM store_sales INNER JOIN date_cte ON ss_sold_date_sk = d_date_sk INNER JOIN customer_demo_cte ON ss_cdemo_sk = cd_demo_sk INNER JOIN promotion_cte ON ss_promo_sk = p_promo_sk), aggregate_pre AS (SELECT ss_item_sk, AVG(ss_quantity) AS avg_quantity, AVG(ss_list_price) AS avg_list_price, AVG(ss_coupon_amt) AS avg_coupon_amt, AVG(ss_sales_price) AS avg_sales_price FROM filtered_sales GROUP BY ss_item_sk), item_join AS (SELECT i_item_id, avg_quantity, avg_list_price, avg_coupon_amt, avg_sales_price FROM aggregate_pre INNER JOIN item ON ss_item_sk = i_item_sk), final_aggregate AS (SELECT i_item_id, MAX(avg_quantity) AS agg1, MAX(avg_list_price) AS agg2, MAX(avg_coupon_amt) AS agg3, MAX(avg_sales_price) AS agg4 FROM item_join GROUP BY i_item_id) SELECT i_item_id, agg1, agg2, agg3, agg4 FROM final_aggregate ORDER BY i_item_id LIMIT 100