WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1213 AND 1224), filtered_stores AS (SELECT s_store_sk, s_state, s_county FROM store WHERE s_store_sk <= 100), prefetched_fact AS (SELECT ss_net_profit, filtered_stores.s_state, filtered_stores.s_county FROM store_sales INNER JOIN filtered_dates ON ss_sold_date_sk = filtered_dates.d_date_sk INNER JOIN filtered_stores ON ss_store_sk = filtered_stores.s_store_sk), state_agg AS (SELECT s_state, SUM(ss_net_profit) AS state_sum, RANK() OVER (ORDER BY SUM(ss_net_profit) DESC) AS ranking FROM prefetched_fact GROUP BY s_state), county_agg AS (SELECT prefetched_fact.s_state, prefetched_fact.s_county, SUM(ss_net_profit) AS county_sum FROM prefetched_fact INNER JOIN state_agg ON prefetched_fact.s_state = state_agg.s_state WHERE state_agg.ranking <= 5 GROUP BY prefetched_fact.s_state, prefetched_fact.s_county), rollup_combined AS (SELECT SUM(county_sum) AS total_sum, s_state, s_county, GROUPING(s_state) + GROUPING(s_county) AS lochierarchy FROM county_agg GROUP BY ROLLUP(s_state, s_county)), windowed AS (SELECT total_sum, s_state, s_county, lochierarchy, RANK() OVER (PARTITION BY lochierarchy, CASE WHEN GROUPING(s_county) = 0 THEN s_state END ORDER BY total_sum DESC) AS rank_within_parent FROM rollup_combined) SELECT total_sum, s_state, s_county, lochierarchy, rank_within_parent FROM windowed ORDER BY lochierarchy DESC, CASE WHEN lochierarchy = 0 THEN s_state END, rank_within_parent LIMIT 100