WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 12 AND d_year = 1998), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand FROM item WHERE i_manager_id = 1), filtered_time AS (SELECT t_time_sk, t_hour, t_minute FROM time_dim WHERE t_meal_time = 'breakfast' OR t_meal_time = 'dinner'), web_sales_joined AS (SELECT ws.ws_ext_sales_price AS ext_price, i.i_brand_id, i.i_brand, t.t_hour, t.t_minute FROM web_sales ws JOIN filtered_date d ON ws.ws_sold_date_sk = d.d_date_sk JOIN filtered_item i ON ws.ws_item_sk = i.i_item_sk JOIN filtered_time t ON ws.ws_sold_time_sk = t.t_time_sk), catalog_sales_joined AS (SELECT cs.cs_ext_sales_price AS ext_price, i.i_brand_id, i.i_brand, t.t_hour, t.t_minute FROM catalog_sales cs JOIN filtered_date d ON cs.cs_sold_date_sk = d.d_date_sk JOIN filtered_item i ON cs.cs_item_sk = i.i_item_sk JOIN filtered_time t ON cs.cs_sold_time_sk = t.t_time_sk), store_sales_joined AS (SELECT ss.ss_ext_sales_price AS ext_price, i.i_brand_id, i.i_brand, t.t_hour, t.t_minute FROM store_sales ss JOIN filtered_date d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_item i ON ss.ss_item_sk = i.i_item_sk JOIN filtered_time t ON ss.ss_sold_time_sk = t.t_time_sk), union_all AS (SELECT ext_price, i_brand_id, i_brand, t_hour, t_minute FROM web_sales_joined UNION ALL SELECT ext_price, i_brand_id, i_brand, t_hour, t_minute FROM catalog_sales_joined UNION ALL SELECT ext_price, i_brand_id, i_brand, t_hour, t_minute FROM store_sales_joined), aggregate AS (SELECT i_brand_id, i_brand, t_hour, t_minute, SUM(ext_price) AS ext_price FROM union_all GROUP BY i_brand_id, i_brand, t_hour, t_minute) SELECT i_brand_id AS brand_id, i_brand AS brand, t_hour, t_minute, ext_price FROM aggregate ORDER BY ext_price DESC, i_brand_id