WITH filtered_cs AS (SELECT cs.cs_item_sk, cs.cs_order_number, cs.cs_quantity, cs.cs_promo_sk, cs.cs_sold_date_sk, d1.d_week_seq, d1.d_date FROM catalog_sales cs JOIN date_dim d1 ON cs.cs_sold_date_sk = d1.d_date_sk JOIN date_dim d3 ON cs.cs_ship_date_sk = d3.d_date_sk JOIN customer_demographics ON cs.cs_bill_cdemo_sk = cd_demo_sk JOIN household_demographics ON cs.cs_bill_hdemo_sk = hd_demo_sk WHERE d1.d_year = 2002 AND d3.d_date > d1.d_date + 5 AND cd_marital_status = 'W' AND hd_buy_potential = '501-1000'), filtered_inv AS (SELECT inv_item_sk, inv_warehouse_sk, inv_date_sk, inv_quantity_on_hand FROM inventory WHERE inv_quantity_on_hand <= 100), cs_inventory_join AS (SELECT cs.cs_item_sk, cs.cs_order_number, cs.cs_promo_sk, inv.inv_warehouse_sk, cs.d_week_seq FROM filtered_cs cs JOIN filtered_inv inv ON cs.cs_item_sk = inv.inv_item_sk AND inv.inv_quantity_on_hand < cs.cs_quantity JOIN date_dim d2 ON inv.inv_date_sk = d2.d_date_sk AND cs.d_week_seq = d2.d_week_seq), dim_joins AS (SELECT i.i_item_desc, w.w_warehouse_name, j.d_week_seq, SUM(CASE WHEN p.p_promo_sk IS NULL THEN 1 ELSE 0 END) AS no_promo, SUM(CASE WHEN p.p_promo_sk IS NOT NULL THEN 1 ELSE 0 END) AS promo, COUNT(*) AS total_cnt FROM cs_inventory_join j JOIN item i ON i.i_item_sk = j.cs_item_sk JOIN warehouse w ON w.w_warehouse_sk = j.inv_warehouse_sk LEFT JOIN promotion p ON p.p_promo_sk = j.cs_promo_sk LEFT JOIN catalog_returns cr ON cr.cr_item_sk = j.cs_item_sk AND cr.cr_order_number = j.cs_order_number GROUP BY i.i_item_desc, w.w_warehouse_name, j.d_week_seq) SELECT i_item_desc, w_warehouse_name, d_week_seq, no_promo, promo, total_cnt FROM dim_joins ORDER BY total_cnt DESC, i_item_desc, w_warehouse_name, d_week_seq LIMIT 100