WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 2 AND d_year IN (2000, 2001, 2002)), date_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_store_sk, ss_hdemo_sk FROM store_sales INNER JOIN filtered_date ON ss_sold_date_sk = d_date_sk), date_sales_store AS (SELECT ss_ticket_number, ss_customer_sk, ss_hdemo_sk FROM date_sales INNER JOIN store ON ss_store_sk = s_store_sk WHERE s_county IN ('Fairfield County','Walker County','Daviess County','Barrow County')), date_sales_store_hd AS (SELECT ss_ticket_number, ss_customer_sk FROM date_sales_store INNER JOIN household_demographics ON ss_hdemo_sk = hd_demo_sk WHERE (hd_buy_potential = '501-1000' OR hd_buy_potential = 'Unknown') AND hd_vehicle_count > 0 AND CASE WHEN hd_vehicle_count > 0 THEN hd_dep_count/hd_vehicle_count ELSE NULL END > 1), aggregated AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM date_sales_store_hd GROUP BY ss_ticket_number, ss_customer_sk), filtered AS (SELECT ss_ticket_number, ss_customer_sk, cnt FROM aggregated WHERE cnt BETWEEN 1 AND 5) SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM filtered INNER JOIN customer ON ss_customer_sk = c_customer_sk ORDER BY cnt DESC, c_last_name ASC