WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 2 AND d_year IN (2000, 2001, 2002)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_county IN ('Fairfield County','Walker County','Daviess County','Barrow County')), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE (hd_buy_potential = '501-1000' OR hd_buy_potential = 'Unknown') AND hd_vehicle_count > 0 AND CASE WHEN hd_vehicle_count > 0 THEN hd_dep_count/hd_vehicle_count ELSE NULL END > 1), sales_with_keys AS (SELECT ss_ticket_number, ss_customer_sk, ss_sold_date_sk, ss_store_sk, ss_hdemo_sk FROM store_sales WHERE ss_sold_date_sk IN (SELECT d_date_sk FROM filtered_date) AND ss_store_sk IN (SELECT s_store_sk FROM filtered_store) AND ss_hdemo_sk IN (SELECT hd_demo_sk FROM filtered_hd)), early_aggregation AS (SELECT ss_ticket_number, ss_customer_sk, ss_sold_date_sk, ss_store_sk, ss_hdemo_sk, COUNT(*) AS cnt FROM sales_with_keys GROUP BY ss_ticket_number, ss_customer_sk, ss_sold_date_sk, ss_store_sk, ss_hdemo_sk), filtered_agg AS (SELECT ss_ticket_number, ss_customer_sk, ss_sold_date_sk, ss_store_sk, ss_hdemo_sk, cnt FROM early_aggregation WHERE cnt BETWEEN 1 AND 5), dimension_join_check AS (SELECT fa.ss_ticket_number, fa.ss_customer_sk, fa.cnt FROM filtered_agg fa INNER JOIN filtered_date fd ON fa.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_store fs ON fa.ss_store_sk = fs.s_store_sk INNER JOIN filtered_hd fh ON fa.ss_hdemo_sk = fh.hd_demo_sk) SELECT c.c_last_name, c.c_first_name, c.c_salutation, c.c_preferred_cust_flag, djc.ss_ticket_number, djc.cnt FROM dimension_join_check djc INNER JOIN customer c ON djc.ss_customer_sk = c.c_customer_sk ORDER BY djc.cnt DESC, c.c_last_name ASC