WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 2 AND d_year IN (2000, 2001, 2002)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_county IN ('Fairfield County', 'Walker County', 'Daviess County', 'Barrow County')), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE (hd_buy_potential = '501-1000' OR hd_buy_potential = 'Unknown') AND hd_vehicle_count > 0 AND CASE WHEN hd_vehicle_count > 0 THEN hd_dep_count / hd_vehicle_count ELSE NULL END > 1), bloom_sales AS (SELECT ss_ticket_number, ss_customer_sk FROM store_sales WHERE EXISTS (SELECT 1 FROM filtered_date WHERE d_date_sk = ss_sold_date_sk) AND EXISTS (SELECT 1 FROM filtered_store WHERE s_store_sk = ss_store_sk) AND EXISTS (SELECT 1 FROM filtered_hd WHERE hd_demo_sk = ss_hdemo_sk)), aggregated AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM bloom_sales GROUP BY ss_ticket_number, ss_customer_sk), filtered AS (SELECT ss_ticket_number, ss_customer_sk, cnt FROM aggregated WHERE cnt BETWEEN 1 AND 5) SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, f.ss_ticket_number, f.cnt FROM filtered f INNER JOIN customer c ON f.ss_customer_sk = c.c_customer_sk ORDER BY f.cnt DESC, c_last_name ASC