WITH date_filter AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year IN (1999, 2000)), customer_filter AS (SELECT c_customer_sk, c_customer_id, c_first_name, c_last_name FROM customer WHERE EXISTS (SELECT 1 FROM store_sales WHERE ss_customer_sk = c_customer_sk AND ss_sold_date_sk IN (SELECT d_date_sk FROM date_filter)) OR EXISTS (SELECT 1 FROM web_sales WHERE ws_bill_customer_sk = c_customer_sk AND ws_sold_date_sk IN (SELECT d_date_sk FROM date_filter))), store_fact AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, d.d_year, ss.ss_net_paid FROM customer_filter c, store_sales ss, date_filter d WHERE c.c_customer_sk = ss.ss_customer_sk AND ss.ss_sold_date_sk = d.d_date_sk), store_agg AS (SELECT customer_id, customer_first_name, customer_last_name, d_year AS year, STDDEV_SAMP(ss_net_paid) AS year_total, 's' AS sale_type FROM store_fact GROUP BY customer_id, customer_first_name, customer_last_name, d_year), web_fact AS (SELECT c.c_customer_id AS customer_id, c.c_first_name AS customer_first_name, c.c_last_name AS customer_last_name, d.d_year, ws.ws_net_paid FROM customer_filter c, web_sales ws, date_filter d WHERE c.c_customer_sk = ws.ws_bill_customer_sk AND ws.ws_sold_date_sk = d.d_date_sk), web_agg AS (SELECT customer_id, customer_first_name, customer_last_name, d_year AS year, STDDEV_SAMP(ws_net_paid) AS year_total, 'w' AS sale_type FROM web_fact GROUP BY customer_id, customer_first_name, customer_last_name, d_year) SELECT s2000.customer_id, s2000.customer_first_name, s2000.customer_last_name FROM store_agg s1999, store_agg s2000, web_agg w1999, web_agg w2000 WHERE s1999.customer_id = s2000.customer_id AND s1999.customer_id = w1999.customer_id AND s1999.customer_id = w2000.customer_id AND s1999.year = 1999 AND s2000.year = 2000 AND w1999.year = 1999 AND w2000.year = 2000 AND s1999.year_total > 0 AND w1999.year_total > 0 AND (CASE WHEN w1999.year_total > 0 THEN w2000.year_total / w1999.year_total ELSE NULL END) > (CASE WHEN s1999.year_total > 0 THEN s2000.year_total / s1999.year_total ELSE NULL END) ORDER BY s2000.customer_first_name, s2000.customer_id, s2000.customer_last_name LIMIT 100